import "../Styles/FlightSearch.css";
import { useNavigate } from "react-router-dom";
import { useState } from "react";
import { SyncLoader } from "react-spinners";

export default function FlightSearch() {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null); // Add state for error messages

  const handleSearch = async (e: any) => {
    e.preventDefault();
    setError(null); // Reset error message

    const departureDateValue = e.target.departure.value;
    const arrivalDateValue = e.target.arrival.value;

    // Check if the dates are selected
    if (!departureDateValue || !arrivalDateValue) {
      setError("Please select both departure and arrival dates.");
      return;
    }

    const departureDate = new Date(departureDateValue);
    const arrivalDate = new Date(arrivalDateValue);
    const today = new Date();

    // Reset time to 00:00:00 to ensure proper date comparison
    today.setHours(0, 0, 0, 0);

    // Check if the departure date is in the past
    if (departureDate < today) {
      setError("Departure date cannot be in the past.");
      return;
    }

    // Check if the arrival date is before the departure date
    if (arrivalDate < departureDate) {
      setError("Arrival date cannot be earlier than the departure date.");
      return;
    }

    setLoading(true);

    try {
      await new Promise((resolve) => setTimeout(resolve, 1000));
      navigate("/flights");
    } catch (error) {
      console.error("Error initiating flight search:", error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="loading">
        <SyncLoader size={25} color={"#082f59"} loading={loading} />
      </div>
    );
  }

  return (
    <div className="scan-grid">
      <h1 className="scan-title">Search for a flight</h1>
      <div className="scan-input">
        <form className="scan-input" onSubmit={handleSearch}>
          <label htmlFor="Departure">From</label>
          <input type="text" name="departure" placeholder="Departure" />
          <label htmlFor="Arrival">To</label>
          <input type="text" name="arrival" placeholder="Destination" />
          <label htmlFor="Departure">Departure Date</label>
          <input type="date" id="departureDate" name="departure" placeholder="Departure Date" />
          <label htmlFor="Arrival">Arrival Date</label>
          <input type="date" name="arrival" id="arrivalDate" placeholder="Arrival Date" />
          {error && <p className="error-message">{error}</p>} {/* Display error message */}
          <button className="scan-button" type="submit">
            Search
          </button>
        </form>
      </div>
    </div>
  );
}
