import "../Styles/FlightSearch.css";
import { useNavigate } from "react-router-dom";
import { useState, useEffect } from "react";
import { SyncLoader } from "react-spinners";
import debounce from "lodash.debounce";
import { searchAirports } from "../Utils/api"; // Import the searchAirports function

export default function FlightSearch() {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // States for airport search
  const [departureQuery, setDepartureQuery] = useState("");
  const [arrivalQuery, setArrivalQuery] = useState("");
  const [departureOptions, setDepartureOptions] = useState<string[]>([]);
  const [arrivalOptions, setArrivalOptions] = useState<string[]>([]);
  const [selectedDeparture, setSelectedDeparture] = useState<string>("");
  const [selectedArrival, setSelectedArrival] = useState<string>("");

  // Debounced function to search airports
  const handleAirportSearch = debounce((query: string, setOptions: (options: string[]) => void) => {
    if (query) {
      searchAirports(query).then((options) => {
        setOptions(options);
      });
    }
  }, 500); // 500ms delay

  // Effect for departure airport search
  useEffect(() => {
    handleAirportSearch(departureQuery, setDepartureOptions);
  }, [departureQuery]);

  // Effect for arrival airport search
  useEffect(() => {
    handleAirportSearch(arrivalQuery, setArrivalOptions);
  }, [arrivalQuery]);

  const handleSearch = async (e: any) => {
    e.preventDefault();
    setError(null);

    const departureDateValue = e.target.departureDate.value;
    const arrivalDateValue = e.target.arrivalDate.value;
    const currency = e.target.currency.value;
    const nonstop = e.target.nonstop.checked;

    if (!departureDateValue || !arrivalDateValue) {
      setError("Please select both departure and arrival dates.");
      return;
    }

    const departureDate = new Date(departureDateValue);
    const arrivalDate = new Date(arrivalDateValue);
    const today = new Date();

    today.setHours(0, 0, 0, 0);
    departureDate.setHours(0, 0, 0, 0);
    arrivalDate.setHours(0, 0, 0, 0);

    if (departureDate < today) {
      setError("Departure date cannot be in the past.");
      return;
    }

    if (arrivalDate < departureDate) {
      setError("Arrival date cannot be earlier than the departure date.");
      return;
    }

    console.log({ selectedDeparture, selectedArrival, departureDate, arrivalDate, currency, nonstop });

    setLoading(true);

    try {
      await new Promise((resolve) => setTimeout(resolve, 1000));
      navigate("/flights");
    } catch (error) {
      console.error("Error initiating flight search:", error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="loading">
        <SyncLoader size={25} color={"#082f59"} loading={loading} />
      </div>
    );
  }

  return (
    <div className="scan-grid">
      <h1 className="scan-title">Search for a flight</h1>
      <div className="scan-input">
        <form className="scan-input" onSubmit={handleSearch}>
          <label htmlFor="departure">From</label>
          <div className="input-with-dropdown">
            <input
              type="text"
              name="departure"
              placeholder="Departure"
              value={departureQuery}
              onChange={(e) => setDepartureQuery(e.target.value)}
            />
            {/* Show a dropdown for departure options */}
            <select
              className="airport-select"
              value={selectedDeparture}
              onChange={(e) => {
                setSelectedDeparture(e.target.value);
                setDepartureQuery(e.target.value); // Automatically fill input with selected IATA code
              }}
            >
              <option value="">Select Airport</option>
              {departureOptions.map((iataCode, index) => (
                <option key={index} value={iataCode}>
                  {iataCode}
                </option>
              ))}
            </select>
          </div>

          <label htmlFor="arrival">To</label>
          <div className="input-with-dropdown">
            <input
              type="text"
              name="arrival"
              placeholder="Destination"
              value={arrivalQuery}
              onChange={(e) => setArrivalQuery(e.target.value)}
            />
            {/* Show a dropdown for arrival options */}
            <select
              className="airport-select"
              value={selectedArrival}
              onChange={(e) => {
                setSelectedArrival(e.target.value);
                setArrivalQuery(e.target.value); // Automatically fill input with selected IATA code
              }}
            >
              <option value="">Select Airport</option>
              {arrivalOptions.map((iataCode, index) => (
                <option key={index} value={iataCode}>
                  {iataCode}
                </option>
              ))}
            </select>
          </div>

          <label htmlFor="departureDate">Departure Date</label>
          <input type="date" id="departureDate" name="departureDate" placeholder="Departure Date" />

          <label htmlFor="arrivalDate">Arrival Date</label>
          <input type="date" name="arrivalDate" id="arrivalDate" placeholder="Arrival Date" />

          <label htmlFor="currency">Currency</label>
          <select name="currency" id="currency">
            <option value="USD">USD</option>
            <option value="EUR">EUR</option>
            <option value="MXN">MXN</option>
          </select>

          <div className="nonstop-checkbox">
            <label>
              <input type="checkbox" name="nonstop" /> Nonstop 
            </label>
          </div>

          {error && <p className="error-message">{error}</p>} {/* Display error message */}

          <button className="scan-button" type="submit">
            Search
          </button>
        </form>
      </div>
    </div>
  );
}
