import { useLocation } from "react-router-dom";

// Segment and FlightData interfaces
interface Segment {
  departureTime: string;
  departureAirportCode: string;
  arrivalTime: string;
  arrivalAirportCode: string;
  airlineCode: string;
  flightNumber: string;
  operatingAirlineCode: string;
  aircraftType: string;
}

interface FlightData {
  id: number;
  initialDepartureTime: string;
  finalArrivalTime: string;
  departureAirportCode: string;
  arrivalAirportCode: string;
  airlineCode: string;
  operatingAirlineCode: string;
  totalDuration: string;
  totalPrice: number;
  pricePerTraveler: number;
  segments: Segment[];
}

const FlightDetail = () => {
  const location = useLocation();
  const flight: FlightData = location.state?.flight; // Get flight data from state

  if (!flight) {
    return <div>No flight data available.</div>;
  }

  return (
    <div className="flight-detail">
      <h1>Flight Details for Flight ID: {flight.id}</h1>
      <p>From: {flight.departureAirportCode}</p>
      <p>To: {flight.arrivalAirportCode}</p>
      <p>Total Price: ${flight.totalPrice}</p>

      {flight.segments.map((segment, index) => (
        <div key={index} className="segment">
          <h3>Segment {index + 1}</h3>
          <p>{segment.departureTime} - {segment.arrivalTime}</p>
          <p>{segment.departureAirportCode} to {segment.arrivalAirportCode}</p>
          <p>Airline: {segment.airlineCode}</p>
        </div>
      ))}
    </div>
  );
};

export default FlightDetail;
