import { useState, useEffect } from "react";
import { getFlight } from "../Utils/api";
import { SyncLoader } from "react-spinners";
import { useNavigate } from "react-router-dom";
import "../Styles/Flights.css"; // Correct path to your CSS file

// Segment and FlightData interfaces
interface Segment {
  departureTime: string;
  departureAirportCode: string;
  arrivalTime: string;
  arrivalAirportCode: string;
  airlineCode: string;
  flightNumber: string;
  operatingAirlineCode: string;
  aircraftType: string;
}

interface FlightData {
  id: number;
  initialDepartureTime: string;
  finalArrivalTime: string;
  departureAirportCode: string;
  arrivalAirportCode: string;
  airlineCode: string;
  operatingAirlineCode: string;
  totalDuration: string;
  totalPrice: number;
  pricePerTraveler: number;
  segments: Segment[];
}

export default function Flights() {
  const [flights, setFlights] = useState<FlightData[] | null>(null);
  const [loading, setLoading] = useState(true);
  const [page, setPage] = useState(0);
  const [size] = useState(5);
  const navigate = useNavigate();

  useEffect(() => {
    setLoading(true);
    getFlight(page, size)
      .then((flightData: FlightData[]) => {
        setFlights(flightData);
      })
      .catch((error) => {
        console.error("Error fetching flight data:", error);
      })
      .finally(() => {
        setLoading(false);
      });
  }, [page, size]);

  const handleFlightClick = (flight: FlightData) => {
    // Navigate to flight detail page and pass flight data as state
    navigate(`/flight-detail`, { state: { flight } });
  };

  if (loading) {
    return (
      <div className="loading">
        <SyncLoader size={25} color={"#ffffff"} loading={loading} />
      </div>
    );
  }

  if (!flights || flights.length === 0) {
    return <div>No flight data available.</div>;
  }

  return (
    <section className="flight-container">
      {flights.map((flight) => (
        <div
          key={flight.id}
          className="flight-box"
          onClick={() => handleFlightClick(flight)} // Pass the entire flight object when clicked
        >
          <div className="flight-info">
            <span>
              {new Date(flight.initialDepartureTime).toLocaleTimeString([], {
                hour: "2-digit",
                minute: "2-digit",
              })}{" "}
              -{" "}
              {new Date(flight.finalArrivalTime).toLocaleTimeString([], {
                hour: "2-digit",
                minute: "2-digit",
              })}
            </span>
          </div>
          <div className="route-info">
            <span>
              {flight.departureAirportCode} - {flight.arrivalAirportCode}
            </span>
          </div>
          <div className="price-info">
            <span>Total: ${flight.totalPrice}</span>
          </div>
        </div>
      ))}
      <div className="pagination-controls">
        <button onClick={() => setPage((prev) => Math.max(prev - 1, 0))} disabled={page === 0}>
          Previous
        </button>
        <button onClick={() => setPage((prev) => prev + 1)}>Next</button>
      </div>
    </section>
  );
}
