import { useState, useEffect } from "react";
import { getFlight } from "../Utils/api";
import "../Styles/Flights.css";
import { SyncLoader } from "react-spinners";

interface Segment {
  departureTime: string;
  departureAirportCode: string;
  arrivalTime: string;
  arrivalAirportCode: string;
  airlineCode: string;
  flightNumber: string;
  operatingAirlineCode: string;
  aircraftType: string;
}

interface FlightData {
  id: number;
  initialDepartureTime: string;
  finalArrivalTime: string;
  departureAirportCode: string;
  arrivalAirportCode: string;
  airlineCode: string;
  operatingAirlineCode: string;
  totalDuration: string;
  totalPrice: number;
  pricePerTraveler: number;
  segments: Segment[];
}

export default function Flights() {
  const [flights, setFlights] = useState<FlightData[] | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    getFlight()
      .then((flightData: FlightData[]) => {
        setFlights(flightData); // Store the flight objects array
      })
      .catch((error) => {
        console.error("Error fetching flight data:", error);
      })
      .finally(() => {
        setLoading(false);
      });
  }, []);

  if (loading) {
    return (
      <div className="loading">
        <SyncLoader size={25} color={"#082f59"} loading={loading} />
      </div>
    );
  }

  if (!flights || flights.length === 0) {
    return <div>No flight data available.</div>;
  }

  return (
    <section className="flight-container">
      {flights.map((flight) => {
        const segments = flight.segments;
        let stop = null;

        if (segments.length > 1) {
          const firstSegment = segments[0];
          const secondSegment = segments[1];
          const stopDurationMs =
            new Date(secondSegment.departureTime).getTime() - new Date(firstSegment.arrivalTime).getTime();
          const stopDurationHours = Math.floor(stopDurationMs / (1000 * 60 * 60));
          const stopDurationMinutes = Math.floor((stopDurationMs % (1000 * 60 * 60)) / (1000 * 60));

          stop = (
            <div>
              <strong>
                Stop at {firstSegment.arrivalAirportCode} for {stopDurationHours}h {stopDurationMinutes}m
              </strong>
            </div>
          );
        }

        return (
          <div className="flight-box" key={flight.id}>
            <div className="flight-details">
              <div className="flight-times">
                <span>
                  {new Date(flight.initialDepartureTime).toLocaleTimeString([], {
                    hour: "2-digit",
                    minute: "2-digit",
                  })}{" "}
                  -{" "}
                  {new Date(flight.finalArrivalTime).toLocaleTimeString([], {
                    hour: "2-digit",
                    minute: "2-digit",
                  })}
                </span>
              </div>
              <div className="flight-route">
                <span>
                  {flight.departureAirportCode} - {flight.arrivalAirportCode}
                </span>
              </div>
              <div className="flight-duration">
                <span>{flight.totalDuration}</span>
              </div>
              <div className="flight-prices">
                <div className="total-price">
                  <span>
                    {flight.totalPrice !== null ? `$${flight.totalPrice} USD` : "N/A"} total
                  </span>
                </div>
                <div className="price-per-traveler">
                  <span>
                    {flight.pricePerTraveler !== null
                      ? `$${flight.pricePerTraveler} USD`
                      : "N/A"}{" "}
                    per Traveler
                  </span>
                </div>
              </div>
              <div className="flight-airline">
                <span>{flight.airlineCode}</span>
              </div>
              {stop && <div>{stop}</div>}
            </div>
          </div>
        );
      })}
    </section>
  );
}
