import { useState, useEffect } from "react";
import { getFlight } from "../Utils/api";
import "../Styles/Flights.css";
import { SyncLoader } from "react-spinners";

// Segment interface outside of FlightData for reuse and clarity
interface Segment {
  departureTime: string;
  departureAirportCode: string;
  arrivalTime: string;
  arrivalAirportCode: string;
  airlineCode: string;
  flightNumber: string;
  operatingAirlineCode: string;
  aircraftType: string;
}

// FlightData interface referencing the Segment interface
interface FlightData {
  id: number; // Unique flight offer ID
  initialDepartureTime: string;
  finalArrivalTime: string;
  departureAirportCode: string;
  arrivalAirportCode: string;
  airlineCode: string;
  operatingAirlineCode: string;
  totalDuration: string;
  totalPrice: number;
  pricePerTraveler: number;
  segments: Segment[]; // Array of flight segments
}

export default function Flights() {
  const [flights, setFlights] = useState<FlightData[]>([]); // Store an array of flights
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    getFlight()
      .then((flightData: FlightData[]) => { // Expecting an array of FlightData
        setFlights(flightData); // Store the array of flight objects
      })
      .catch((error) => {
        console.error("Error fetching flight data:", error);
      })
      .finally(() => {
        setLoading(false);
      });
  }, []);

  if (loading) {
    return (
      <div className="loading">
        <SyncLoader size={25} color={"#082f59"} loading={loading} />
      </div>
    );
  }

  if (!flights || flights.length === 0) {
    return <div>No flight data available.</div>;
  }

  return (
    <section className="flight-container">
      {flights.map((flight) => (
        <div key={flight.id} className="flight-box">
          <div className="flight-details">
            {/* Display initial and final departure time */}
            <div className="flight-times">
              <span>
                {new Date(flight.initialDepartureTime).toLocaleString()} - {new Date(flight.finalArrivalTime).toLocaleString()}
              </span>
            </div>

            {/* Display departure and arrival airport (name + code) */}
            <div className="flight-route">
              <span>
                {flight.departureAirportCode} - {flight.arrivalAirportCode}
              </span>
            </div>

            {/* Display airline name and code */}
            <div className="flight-airline">
              <span>Airline: {flight.airlineCode}</span>
            </div>

            {/* Display operating airline name and code (only if different from the main airline) */}
            {flight.operatingAirlineCode && flight.operatingAirlineCode !== flight.airlineCode && (
              <div className="flight-operating-airline">
                <span>Operating Airline: {flight.operatingAirlineCode}</span>
              </div>
            )}

            {/* Display total duration of the flight */}
            <div className="flight-duration">
              <span>Total Duration: {flight.totalDuration}</span>
            </div>

            {/* Display stop information if there are stops */}
            {flight.segments.length > 1 && (
              <div className="flight-stops">
                <h4>Stops:</h4>
                {flight.segments.map((segment, index) => (
                  <div key={index}>
                    <p>
                      Stop {index + 1}: {segment.departureAirportCode} to {segment.arrivalAirportCode} - Flight Time:{" "}
                      {new Date(segment.departureTime).toLocaleString()} to {new Date(segment.arrivalTime).toLocaleString()}
                    </p>
                  </div>
                ))}
              </div>
            )}

            {/* Display total price and price per traveler */}
            <div className="flight-prices">
              <div className="total-price">
                <span>Total Price: {flight.totalPrice ? `$${flight.totalPrice}` : "N/A"} USD</span>
              </div>
              <div className="price-per-traveler">
                <span>Price per Traveler: {flight.pricePerTraveler ? `$${flight.pricePerTraveler}` : "N/A"} USD</span>
              </div>
            </div>
          </div>
        </div>
      ))}
    </section>
  );
}
