import { useState, useEffect } from "react";
import { getFlight } from "../Utils/api";
import "../Styles/Flights.css";
import { SyncLoader } from "react-spinners";

interface FlightData {
  flightId: string;
  initialDepartureDateTime: string;
  finalArrivalDateTime: string;
  departureAirportName: string;
  departureAirportCode: string;
  arrivalAirportName: string;
  arrivalAirportCode: string;
  airlineName: string;
  airlineCode: string;
  operatingAirlineName?: string; // Optional if different from the main airline
  operatingAirlineCode?: string; // Optional if different from the main airline
  totalFlightDuration: string;
  stops?: Array<{
    airportName: string;
    airportCode: string;
    layoverTime: string;
  }>;
  totalPrice: number | null;
  pricePerTraveler: number | null;
  segments: Array<{
    departureTime: string;
    departureAirportCode: string;
    arrivalTime: string;
    arrivalAirportCode: string;
    airlineCode: string;
    flightNumber: string;
    operatingAirlineCode: string;
    aircraftType: string;
  }>;
}


export default function Flights() {
  const [flight, setFlight] = useState<FlightData | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    getFlight()
      .then((flightData: FlightData) => {
        setFlight(flightData);
      })
      .catch((error) => {
        console.error("Error fetching flight data:", error);
      })
      .finally(() => {
        setLoading(false);
      });
  }, []);

  if (loading) {
    return (
      <div className="loading">
        <SyncLoader size={25} color={"#082f59"} loading={loading} />
      </div>
    );
  }

  if (!flight) {
    return <div>No flight data available.</div>;
  }

  return (
    <section className="flight-container">
      {loading ? (
        <div className="loading">
          <SyncLoader size={25} color={"#082f59"} loading={loading} />
        </div>
      ) : (
        flights && (
          <div>
            {flights.map((flight) => (
              <div key={flight.flightId} className="flight-box">
                <div className="flight-details">
                  <div className="time-route">
                    <p>{new Date(flight.initialDepartureDateTime).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })} - {new Date(flight.finalArrivalDateTime).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}</p>
                    <p>{flight.departureAirportName} ({flight.departureAirportCode}) - {flight.arrivalAirportName} ({flight.arrivalAirportCode})</p>
                  </div>
                  <div className="duration">
                    <p>{flight.totalFlightDuration} (Nonstop)</p>
                  </div>
                  <div className="airline">
                    <p>{flight.airlineName} ({flight.airlineCode})</p>
                  </div>
                  <div className="pricing">
                    <p>${flight.totalPrice} MXN <span>total</span></p>
                    <p>${flight.pricePerTraveler} MXN <span>per Traveler</span></p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )
      )}
    </section>
  );
}
