import { useState, useEffect } from "react";
import { getFlight } from "../Utils/api";
import { Link, useLocation } from "react-router-dom";
import "../Styles/Flights.css";
import "../Styles/Navbar.css";
interface FlightData {
  flightId: string;
  initialDepartureDateTime: string;
  finalArrivalDateTime: string;
  departureAirportName: string;
  departureAirportCode: string;
  arrivalAirportName: string;
  arrivalAirportCode: string;
  airlineName: string;
  airlineCode: string;
  operatingAirlineName?: string; // Optional if different from the main airline
  operatingAirlineCode?: string; // Optional if different from the main airline
  totalFlightDuration: string;
  stops?: Array<{
    airportName: string;
    airportCode: string;
    layoverTime: string;
  }>;
  totalPrice: number;
  pricePerTraveler: number;
}

export default function Flights() {
  const [flights, setFlights] = useState<FlightData[] | null>(null);
  const [isSmallerNavbar, setIsSmallerNavbar] = useState(false);
  const location = useLocation();
  const smallerNavbarRoutes = ["/Flights"];

  useEffect(() => {
    const queryParams = new URLSearchParams(location.search);
    const departure = queryParams.get("departure");
    const arrival = queryParams.get("arrival");

    if (departure && arrival) {
      getFlight(departure, arrival)
        .then((flightData: FlightData[] | null) => {
          setFlights(flightData);
        })
        .catch((error) => {
          console.error("Error fetching flight data:", error);
        });
    }

    if (smallerNavbarRoutes.includes(location.pathname)) {
      setIsSmallerNavbar(true);
    } else {
      setIsSmallerNavbar(false);
    }
  }, [location, location.pathname]);

  return (
    <section>
      <section className="flight-container">
        {flights && (
          <div>
            {flights.map((flight) => (
              <div key={flight.flightId} className="flight-box">
                <h3>{flight.airlineName} ({flight.airlineCode})</h3>
                {flight.operatingAirlineName && flight.operatingAirlineName !== flight.airlineName && (
                  <p>
                    Operating Airline: {flight.operatingAirlineName} ({flight.operatingAirlineCode})
                  </p>
                )}
                <p>
                  Departure: {flight.departureAirportName} ({flight.departureAirportCode}) at {flight.initialDepartureDateTime}
                </p>
                <p>
                  Arrival: {flight.arrivalAirportName} ({flight.arrivalAirportCode}) at {flight.finalArrivalDateTime}
                </p>
                <p>Total Duration: {flight.totalFlightDuration}</p>
                {flight.stops && flight.stops.length > 0 && (
                  <div>
                    <h4>Stops:</h4>
                    {flight.stops.map((stop, index) => (
                      <p key={index}>
                        {stop.airportName} ({stop.airportCode}) - Layover: {stop.layoverTime}
                      </p>
                    ))}
                  </div>
                )}
                <p>Total Price: {flight.totalPrice} USD</p>
                <p>Price per Traveler: {flight.pricePerTraveler} USD</p>
                <Link to={flight.flightId}>
                  <button>View Flight</button>
                </Link>
              </div>
            ))}
          </div>
        )}
      </section>
    </section>
  );
}
