import { useState, useEffect } from "react";
import { getFlight } from "../Utils/api";
import "../Styles/Flights.css";
import { SyncLoader } from "react-spinners";

// Segment interface outside of FlightData for reuse and clarity
interface Segment {
  departureTime: string;
  departureAirportCode: string;
  arrivalTime: string;
  arrivalAirportCode: string;
  airlineCode: string;
  flightNumber: string;
  operatingAirlineCode: string;
  aircraftType: string;
}

// FlightData interface referencing the Segment interface
interface FlightData {
  id: number; // Unique flight offer ID
  initialDepartureTime: string;
  finalArrivalTime: string;
  departureAirportCode: string;
  arrivalAirportCode: string;
  airlineCode: string;
  operatingAirlineCode: string;
  totalDuration: string;
  totalPrice: number;
  pricePerTraveler: number;
  segments: Segment[]; // Array of flight segments
}

export default function Flights() {
  const [flights, setFlights] = useState<FlightData[]>([]); // Store an array of flights
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    getFlight()
      .then((flightData: FlightData[]) => { // Expecting an array of FlightData
        setFlights(flightData); // Store the array of flight objects
      })
      .catch((error) => {
        console.error("Error fetching flight data:", error);
      })
      .finally(() => {
        setLoading(false);
      });
  }, []);

  if (loading) {
    return (
      <div className="loading">
        <SyncLoader size={25} color={"#082f59"} loading={loading} />
      </div>
    );
  }

  if (!flights || flights.length === 0) {
    return <div>No flight data available.</div>;
  }

  return (
    <section className="flight-container">
      {flights.map((flight) => (
        <div key={flight.id} className="flight-box">
          <div className="flight-details">
            <div className="flight-times">
              <span>
                {new Date(flight.initialDepartureTime).toLocaleTimeString([], {
                  hour: '2-digit',
                  minute: '2-digit',
                })}{" "}
                -{" "}
                {new Date(flight.finalArrivalTime).toLocaleTimeString([], {
                  hour: '2-digit',
                  minute: '2-digit',
                })}
              </span>
            </div>
            <div className="flight-route">
              <span>
                {flight.departureAirportCode} - {flight.arrivalAirportCode}
              </span>
            </div>
            <div className="flight-duration">
              <span>{flight.totalDuration}</span>
            </div>
            <div className="flight-prices">
              <div className="total-price">
                <span>
                  {flight.totalPrice !== null ? `$${flight.totalPrice} USD` : "N/A"} total
                </span>
              </div>
              <div className="price-per-traveler">
                <span>
                  {flight.pricePerTraveler !== null
                    ? `$${flight.pricePerTraveler} USD`
                    : "N/A"}{" "}
                  per Traveler
                </span>
              </div>
            </div>
            <div className="flight-airline">
              <span>{flight.airlineCode}</span>
            </div>
          </div>

          {flight.segments.length > 0 && (
            <div>
              <h4>Segments:</h4>
              {flight.segments.map((segment, index) => (
                <div key={index}>
                  <p>
                    Segment {index + 1}: {segment.departureAirportCode} to {segment.arrivalAirportCode} <br />
                    Flight {segment.flightNumber} ({segment.airlineCode}), Aircraft: {segment.aircraftType}
                  </p>
                </div>
              ))}
            </div>
          )}
        </div>
      ))}
    </section>
  );
}
