import { useState, useEffect } from "react";
import { fetchFlightOffers } from "../Utils/api"; // Use the dynamic flight offer function
import "../Styles/Flights.css";
import { SyncLoader } from "react-spinners";
import { useNavigate, useLocation } from "react-router-dom";

export default function Flights() {
  const [flights, setFlights] = useState<FlightData[] | null>(null);
  const [loading, setLoading] = useState(true);
  const [page, setPage] = useState(0);
  const [size] = useState(5); // Default size of 5 records per page
  const navigate = useNavigate(); 
  const location = useLocation(); // Get location to retrieve search parameters

  const {
    selectedDeparture,
    selectedArrival,
    departureDate,
    returnDate,
    currency,
    nonstop,
  } = location.state; // Extract search parameters

  useEffect(() => {
    setLoading(true);

    fetchFlightOffers(selectedDeparture, selectedArrival, departureDate, returnDate, currency, nonstop)
      .then((flightData: FlightData[]) => {
        setFlights(flightData);
      })
      .catch((error) => {
        console.error("Error fetching flight data:", error);
      })
      .finally(() => {
        setLoading(false);
      });
  }, [selectedDeparture, selectedArrival, departureDate, returnDate, currency, nonstop]);

  const handleFlightClick = (flight: FlightData) => {
    navigate("/flight-detail", { state: { flight } });
  };

  if (loading) {
    return (
      <div className="loading">
        <SyncLoader size={25} color={"#ffffff"} loading={loading} />
      </div>
    );
  }

  if (!flights || flights.length === 0) {
    return <div>No flight data available.</div>;
  }

  return (
    <section className="flight-container">
      {flights.map((flight) => (
        <div
          key={flight.id}
          className="flight-box"
          onClick={() => handleFlightClick(flight)}
        >
          {/* Flight details */}
        </div>
      ))}
    </section>
  );
}
