import axios from "axios";

process.env.NODE_TLS_REJECT_UNAUTHORIZED = "0";

interface FlightData {
  flightId: string;
  initialDepartureDateTime: string;
  finalArrivalDateTime: string;
  departureAirportName: string;
  departureAirportCode: string;
  arrivalAirportName: string;
  arrivalAirportCode: string;
  airlineName: string;
  airlineCode: string;
  operatingAirlineName?: string; // Optional if different from the main airline
  operatingAirlineCode?: string; // Optional if different from the main airline
  totalFlightDuration: string;
  stops?: Array<{
    airportName: string;
    airportCode: string;
    layoverTime: string;
  }>;
  totalPrice: number;
  pricePerTraveler: number;
}


export const getFlight = (): Promise<FlightData> => {
  return axios
    .get(`http://localhost:8080/mock-fetch-flight-summary`)
    .then((res: any) => {
      if (!res.data) {
        throw new Error("No data returned from API");
      }

      const flight = res.data;

      if (!flight.initialDepartureTime || !flight.finalArrivalTime || !flight.totalDuration || !flight.segments) {
        throw new Error("Incomplete flight data");
      }

      return {
        initialDepartureTime: flight.initialDepartureTime,
        finalArrivalTime: flight.finalArrivalTime,
        departureAirportName: flight.initialDepartureAirportName || "Unknown",
        departureAirportCode: flight.initialDepartureAirportCode || "Unknown",
        arrivalAirportName: flight.finalArrivalAirportName || "Unknown",
        arrivalAirportCode: flight.finalArrivalAirportCode || "Unknown",
        airlineName: flight.airlineName || "Unknown",
        airlineCode: flight.airlineCode || "Unknown",
        operatingAirlineName: flight.operatingAirlineName || flight.airlineName,
        operatingAirlineCode: flight.operatingAirlineCode || flight.airlineCode,
        totalDuration: flight.totalDuration,
        stops: flight.stops || [],
        totalPrice: flight.totalPrice || null,
        pricePerTraveler: flight.pricePerTraveler || null,
        segments: flight.segments || [],
      };
    });
};

export const getSingleFlight = (flightId: string) => {
  return axios
    .get(`https://localhost:7039/api/Flight/${flightId}`)
    .then((response: any) => {
      return response.data;
    });
};

export const getUser = (userId: number) => {
  return axios
    .get(`https://localhost:7039/api/User/${userId}`)
    .then((response) => {
      return response.data;
    });
};

export const postBooking = (
  userId: number,
  FlightId: string,
  numPassengers: number
) => {
  const postBody = {
    numPassengers: numPassengers,
  };

  return axios
    .post(
      `https://localhost:7039/api/Bookings?UserId=${userId}&FlightId=${FlightId}`,
      postBody
    )
    .then((response) => {
      return response.data;
    });
};

export const getBookings = (userId: number) => {
  return axios
    .get(`https://localhost:7039/api/Bookings?UserId=${userId}`)
    .then((response) => {
      return response.data;
    });
};
