import { useState, useEffect } from "react";
import { getFlight } from "../Utils/api";
import "../Styles/Flights.css";
import "../Styles/Navbar.css";
import { SyncLoader } from "react-spinners";

interface FlightData {
  initialDepartureTime: string;
  finalArrivalTime: string;
  departureAirportName: string;
  departureAirportCode: string;
  arrivalAirportName: string;
  arrivalAirportCode: string;
  airlineName: string;
  airlineCode: string;
  operatingAirlineName?: string;
  operatingAirlineCode?: string;
  totalDuration: string;
  stops?: Array<{
    airportName: string;
    airportCode: string;
    layoverTime: string;
  }>;
  totalPrice?: number | null;
  pricePerTraveler?: number | null;
  segments: Array<{
    departureTime: string;
    departureAirportCode: string;
    arrivalTime: string;
    arrivalAirportCode: string;
    airlineCode: string;
    flightNumber: string;
    operatingAirlineCode: string;
    aircraftType: string;
  }>;
}

export default function Flights() {
  const [flight, setFlight] = useState<FlightData | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    getFlight()
      .then((flightData: FlightData | null) => {
        setFlight(flightData);
      })
      .catch((error) => {
        console.error("Error fetching flight data:", error);
      })
      .finally(() => {
        setLoading(false);
      });
  }, []);

  return (
    <section>
      {loading ? (
        <div className="loading">
          <SyncLoader size={25} color={"#082f59"} loading={loading} />
        </div>
      ) : (
        flight && (
          <section className="flight-container">
            <div className="flight-box">
              <h3>
                {flight.airlineName} ({flight.airlineCode})
              </h3>
              {flight.operatingAirlineName &&
                flight.operatingAirlineName !== flight.airlineName && (
                  <p>
                    Operating Airline: {flight.operatingAirlineName} (
                    {flight.operatingAirlineCode})
                  </p>
                )}
              <p>
                Departure: {flight.departureAirportName} (
                {flight.departureAirportCode}) at{" "}
                {flight.initialDepartureTime}
              </p>
              <p>
                Arrival: {flight.arrivalAirportName} (
                {flight.arrivalAirportCode}) at {flight.finalArrivalTime}
              </p>
              <p>Total Duration: {flight.totalDuration}</p>
              {flight.segments && flight.segments.length > 0 && (
                <div>
                  <h4>Segments:</h4>
                  {flight.segments.map((segment, index) => (
                    <p key={index}>
                      {segment.departureAirportCode} to {segment.arrivalAirportCode} -
                      Flight: {segment.flightNumber}, Aircraft: {segment.aircraftType}
                    </p>
                  ))}
                </div>
              )}
              <p>Total Price: {flight.totalPrice || "N/A"} USD</p>
              <p>Price per Traveler: {flight.pricePerTraveler || "N/A"} USD</p>
            </div>
          </section>
        )
      )}
    </section>
  );
}
