import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import { getFlightById } from "../Utils/api"; // You need to implement this function to fetch flight by ID

interface Segment {
  departureTime: string;
  arrivalTime: string;
  departureAirportCode: string;
  arrivalAirportCode: string;
  airlineCode: string;
  flightNumber: string;
  operatingAirlineCode: string;
  aircraftType: string;
}

interface FlightData {
  id: number;
  initialDepartureTime: string;
  finalArrivalTime: string;
  departureAirportCode: string;
  arrivalAirportCode: string;
  airlineCode: string;
  operatingAirlineCode: string;
  totalDuration: string;
  totalPrice: number;
  pricePerTraveler: number;
  segments: Segment[];
}

const FlightDetail = () => {
  const { id } = useParams<{ id: string }>(); // Get the flight ID from the URL
  const [flight, setFlight] = useState<FlightData | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setLoading(true);
    getFlightById(id) // Fetch flight by ID
      .then((data) => {
        setFlight(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching flight details:", error);
        setLoading(false);
      });
  }, [id]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (!flight) {
    return <div>Flight not found</div>;
  }

  return (
    <div className="flight-detail">
      <h1>Flight Details for Flight ID: {flight.id}</h1>
      <p>From: {flight.departureAirportCode}</p>
      <p>To: {flight.arrivalAirportCode}</p>
      <p>Total Price: ${flight.totalPrice}</p>

      {flight.segments.map((segment, index) => (
        <div key={index} className="segment">
          <h3>Segment {index + 1}</h3>
          <p>
            {segment.departureTime} - {segment.arrivalTime}
          </p>
          <p>
            {segment.departureAirportCode} to {segment.arrivalAirportCode}
          </p>
          <p>Airline: {segment.airlineCode}</p>
        </div>
      ))}
    </div>
  );
};

export default FlightDetail;
