import "../Styles/FlightSearch.css";
import { useNavigate } from "react-router-dom";
import { useState } from "react";
import { SyncLoader } from "react-spinners";
import debounce from 'lodash/debounce';
import { searchAirports } from "../Utils/api";

export default function FlightSearch() {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [departureOptions, setDepartureOptions] = useState<string[]>([]);
  const [arrivalOptions, setArrivalOptions] = useState<string[]>([]);

  const fetchAirportOptions = debounce(async (keyword: string, setOptions: (options: string[]) => void) => {
    if (keyword.length > 2) {
      try {
        const options = await searchAirports(keyword);
        setOptions(options);
      } catch (error) {
        console.error("Error fetching airport data:", error);
      }
    }
  }, 500); // Debounce the API calls to avoid excessive requests

  const handleDepartureChange = (e: any) => {
    fetchAirportOptions(e.target.value, setDepartureOptions);
  };

  const handleArrivalChange = (e: any) => {
    fetchAirportOptions(e.target.value, setArrivalOptions);
  };

  return (
    <div className="scan-grid">
      <h1 className="scan-title">Search for a flight</h1>
      <div className="scan-input">
        <form className="scan-input">
          <label htmlFor="departure">From</label>
          <input
            type="text"
            name="departure"
            placeholder="Departure"
            onChange={handleDepartureChange} // Call API on departure input change
          />
          {departureOptions.length > 0 && (
            <ul>
              {departureOptions.map((iataCode, index) => (
                <li key={index}>{iataCode}</li>
              ))}
            </ul>
          )}

          <label htmlFor="arrival">To</label>
          <input
            type="text"
            name="arrival"
            placeholder="Destination"
            onChange={handleArrivalChange} // Call API on arrival input change
          />
          {arrivalOptions.length > 0 && (
            <ul>
              {arrivalOptions.map((iataCode, index) => (
                <li key={index}>{iataCode}</li>
              ))}
            </ul>
          )}
        </form>
      </div>
    </div>
  );
}
