import { useLocation,useNavigate} from "react-router-dom";
import "../Styles/FlightDetail.css";


export default function FlightDetail() {
  const location = useLocation();
  const flight = location.state?.flight;

  if (!flight) {
    return <div>No flight details available.</div>;
  }

  return (
    <section className="flight-detail-container">
      <h2>Flight Details</h2>
      <p>
        {flight.departureAirportName} ({flight.departureAirportCode}) to{" "}
        {flight.arrivalAirportName} ({flight.arrivalAirportCode})
      </p>
      <p>
        Departure: {new Date(flight.initialDepartureDateTime).toLocaleString()}
      </p>
      <p>
        Arrival: {new Date(flight.finalArrivalDateTime).toLocaleString()}
      </p>
      <p>Total Duration: {flight.totalFlightDuration}</p>
      <p>Airline: {flight.airlineName} ({flight.airlineCode})</p>
      {flight.operatingAirlineName && flight.operatingAirlineName !== flight.airlineName && (
        <p>Operating Airline: {flight.operatingAirlineName} ({flight.operatingAirlineCode})</p>
      )}
      <h3>Segments</h3>
      {flight.segments.map((segment, index) => (
        <div key={index}>
          <p>Flight {segment.flightNumber} - {segment.aircraftType}</p>
          <p>
            {segment.departureAirportCode} to {segment.arrivalAirportCode}{" "}
            - Departure: {new Date(segment.departureTime).toLocaleString()} - Arrival:{" "}
            {new Date(segment.arrivalTime).toLocaleString()}
          </p>
        </div>
      ))}
      <h3>Pricing</h3>
      <p>Total Price: {flight.totalPrice || "N/A"} USD</p>
      <p>Price per Traveler: {flight.pricePerTraveler || "N/A"} USD</p>
    </section>
  );
}
