import "../Styles/FlightSearch.css";
import { useState } from "react";
import { SyncLoader } from "react-spinners";
import { getFlight } from "../Utils/api"; // Import the API function

interface FlightData {
  flightId: string;
  initialDepartureDateTime: string;
  finalArrivalDateTime: string;
  departureAirportName: string;
  departureAirportCode: string;
  arrivalAirportName: string;
  arrivalAirportCode: string;
  airlineName: string;
  airlineCode: string;
  operatingAirlineName?: string;
  operatingAirlineCode?: string;
  totalFlightDuration: string;
  stops?: Array<{
    airportName: string;
    airportCode: string;
    layoverTime: string;
  }>;
  totalPrice: number;
  pricePerTraveler: number;
}

export default function FlightSearch() {
  const [loading, setLoading] = useState(false);
  const [flightData, setFlightData] = useState<FlightData[]>([]);
  const [departure, setDeparture] = useState("");
  const [arrival, setArrival] = useState("");

  const handleSearch = async (e: any) => {
    e.preventDefault();
    setLoading(true);

    // Start a timer for 2 seconds
    const timer = new Promise<void>((resolve) => setTimeout(resolve, 2000));

    try {
      // Fetch flight data from API
      const dataPromise = getFlight();

      // Wait for both the timer and the API call to complete
      const [data] = await Promise.all([dataPromise, timer]);

      setFlightData(data);  // Store the fetched data in state
    } catch (error) {
      console.error("Error fetching flight data:", error);
    } finally {
      setLoading(false);  // Stop the loading spinner after the delay
    }
  };

  return (
    <div className="scan-grid">
      <h1 className="scan-title">Search for a flight</h1>
      <div className="scan-input">
        <form className="scan-input" onSubmit={handleSearch}>
          <label htmlFor="Departure">From</label>
          <input
            type="text"
            name="departure"
            placeholder="Departure"
            value={departure}
            onChange={(e) => setDeparture(e.target.value)}
          />
          <label htmlFor="Arrival">To</label>
          <input
            type="text"
            name="arrival"
            placeholder="Destination"
            value={arrival}
            onChange={(e) => setArrival(e.target.value)}
          />
          <label htmlFor="Departure">Departure Date</label>
          <input
            type="date"
            id="departureDate"
            name="departure"
            placeholder="Departure Date"
          />
          <label htmlFor="Arrival"> Arrival Date </label>
          <input
            type="date"
            name="arrival"
            id="arrivalDate"
            placeholder="Arrival Date"
          />
          <button className="scan-button" type="submit">
            Search
          </button>
        </form>
      </div>

      {loading ? (
        <div className="loading">
          <SyncLoader size={25} color={"#082f59"} loading={loading} />
        </div>
      ) : (
        <div className="flight-results">
          {flightData.length > 0 ? (
            flightData.map((flight) => (
              <div key={flight.flightId} className="flight-box">
                <h3>{flight.airlineName} ({flight.airlineCode})</h3>
                {flight.operatingAirlineName && flight.operatingAirlineName !== flight.airlineName && (
                  <p>
                    Operating Airline: {flight.operatingAirlineName} ({flight.operatingAirlineCode})
                  </p>
                )}
                <p>
                  Departure: {flight.departureAirportName} ({flight.departureAirportCode}) at {flight.initialDepartureDateTime}
                </p>
                <p>
                  Arrival: {flight.arrivalAirportName} ({flight.arrivalAirportCode}) at {flight.finalArrivalDateTime}
                </p>
                <p>Total Duration: {flight.totalFlightDuration}</p>
                {flight.stops && flight.stops.length > 0 && (
                  <div>
                    <h4>Stops:</h4>
                    {flight.stops.map((stop, index) => (
                      <p key={index}>
                        {stop.airportName} ({stop.airportCode}) - Layover: {stop.layoverTime}
                      </p>
                    ))}
                  </div>
                )}
                <p>Total Price: {flight.totalPrice} USD</p>
                <p>Price per Traveler: {flight.pricePerTraveler} USD</p>
              </div>
            ))
          ) : (
            <p>No flights found.</p>
          )}
        </div>
      )}
    </div>
  );
}
