import axios from "axios";

// Segment interface matching the inner Segment class in Java
export interface Segment {
  departureTime: string;
  departureAirportCode: string;
  arrivalTime: string;
  arrivalAirportCode: string;
  airlineCode: string;
  flightNumber: string;
  operatingAirlineCode: string;
  aircraftType: string;
}

// FlightData interface matching the structure of the Java FlightSummary class
export interface FlightData {
  id: number; // Unique flight offer ID
  initialDepartureTime: string;
  finalArrivalTime: string;
  departureAirportCode: string;
  arrivalAirportCode: string;
  airlineCode: string;
  operatingAirlineCode: string;
  totalDuration: string;
  totalPrice: number;
  pricePerTraveler: number;
  segments: Segment[]; // Array of flight segments
}

// Fetch flight summary from the backend service
export const getFlight = (): Promise<FlightData[]> => {
  return axios
    .get(`http://localhost:8080/mock-fetch-flight-summary`)
    .then((res: any) => {
      const flights = res.data;

      // Map through flight summaries and ensure each matches the FlightData interface
      const flightDataList: FlightData[] = flights.map((flight: any) => ({
        id: flight.id,
        initialDepartureTime: flight.initialDepartureTime,
        finalArrivalTime: flight.finalArrivalTime,
        departureAirportCode: flight.departureAirportCode,
        arrivalAirportCode: flight.arrivalAirportCode,
        airlineCode: flight.airlineCode,
        operatingAirlineCode: flight.operatingAirlineCode,
        totalDuration: flight.totalDuration,
        totalPrice: flight.totalPrice,
        pricePerTraveler: flight.pricePerTraveler,
        segments: flight.segments.map((segment: any) => ({
          departureTime: segment.departureTime,
          departureAirportCode: segment.departureAirportCode,
          arrivalTime: segment.arrivalTime,
          arrivalAirportCode: segment.arrivalAirportCode,
          airlineCode: segment.airlineCode,
          flightNumber: segment.flightNumber,
          operatingAirlineCode: segment.operatingAirlineCode,
          aircraftType: segment.aircraftType,
        })),
      }));

      return flightDataList;
    })
    .catch((error) => {
      console.error("Error fetching flight data:", error);
      return [];
    });
};
