import { useState, useEffect } from "react";
import { getFlight } from "../Utils/api";
import { Link, useLocation } from "react-router-dom";
import "../Styles/Flights.css";
import "../Styles/Navbar.css";
import { SyncLoader } from "react-spinners";
interface FlightData {
  flightId: string;
  initialDepartureDateTime: string;
  finalArrivalDateTime: string;
  departureAirportName: string;
  departureAirportCode: string;
  arrivalAirportName: string;
  arrivalAirportCode: string;
  airlineName: string;
  airlineCode: string;
  operatingAirlineName?: string;
  operatingAirlineCode?: string;
  totalFlightDuration: string;
  stops?: Array<{
    airportName: string;
    airportCode: string;
    layoverTime: string;
  }>;
  totalPrice: number;
  pricePerTraveler: number;
}

export default function Flights() {
  const [flights, setFlights] = useState<FlightData[] | null>(null);

  const [isSmallerNavbar, setIsSmallerNavbar] = useState(false);

  const location = useLocation();

  const smallerNavbarRoutes = ["/Flights"];

  useEffect(() => {
    const queryParams = new URLSearchParams(location.search);
    const departure = queryParams.get("departure");
    const arrival = queryParams.get("arrival");

    if (departure && arrival) {
      getFlight()
        .then((flightData: FlightData[] | null) => {
          setFlights(flightData);
        })
        .catch((error) => {
          console.error("Error fetching flight data:", error);
        });
    }

    if (smallerNavbarRoutes.includes(location.pathname)) {
      setIsSmallerNavbar(true);
    } else {
      setIsSmallerNavbar(false);
    }
  }, [location, location.pathname]);

  return (
    <section>
      <section className="flight-container">
        {flights && (
          <div>
            {flights.map((flight) => (
              <div key={flight.flightId} className="flight-box">
                <h3>{flight.airlineName}</h3>
                <p>
                  {flight.departureAirportName} - {flight.arrivalAirportName}
                </p>
                <p>
                  Departure Date:{" "}
                  {flight.initialDepartureDateTime.replace(/-/g, "/").slice(0, 10)}
                </p>
                <p>
                  Arrival Date:{" "}
                  {flight.finalArrivalDateTime.replace(/-/g, "/").slice(0, 10)}
                </p>
                <Link to={flight.flightId}>
                  <button>View Flight</button>
                </Link>
              </div>
            ))}
          </div>
        )}
      </section>
    </section>
  );
}
